import React , { Component } from 'react';
import axios from 'axios';
import {API_BASE_URL } from '../../constants/apiConstants';
import { withRouter,Switch, Route, Link } from "react-router-dom";
//import {Route, IndexRoute,browserHistory} from 'react-router';
import { Router,browserHistory } from 'react-router';

class EditEmployee extends Component {

    constructor(props) {
        super(props);
        this.state = {
            input: {},
            errors: {},
            employees: [],
            employeeId:'',
            successMessage:''
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

    componentDidMount(){
        console.log(window.location.pathname); //yields: "/js" (where snippets run)
        //console.log(window.location.href);     //yields: "https://stacksnippets.net/js"
        let urlElements = window.location.pathname.split('/');
        let employeeIds = urlElements[3];
        //debugger
        this.state.employeeId = urlElements[3];
        //this.setState({ employeeId:urlElements[3] })
        console.log(this.state.employeeId);
        this.getEmployeeDetails();
    } 

    getEmployeeDetails= () => {
        let self =this;
        axios.get(API_BASE_URL+'api/employees/'+self.state.employeeId)
        .then(function (response) {
             //console.log(response)
            if(response.status === 200){
                self.setState({ employees: response.data })
            }
             console.log(self.state.employees)
        })
        .catch(function (error) {
            console.log(error);
        });    
    }

    handleChange(event) {
        let input = this.state.input;
        let errors = this.state.errors;
        input[event.target.name] = event.target.value;
        errors[event.target.name] = '';
        this.setState({
          input
        });
        //this.validate();
    }

    handleSubmit(event) {
        event.preventDefault();
        let self = this;
        if(this.validate()){
            console.log(this.state);

            let input = {};
            input["employee_name"] = "";
            input["employee_salary"] = "";
            input["employee_age"] = "";
            input["email"] = "";
            input["mobile_number"] = "";
            input["address"] = "";
            this.setState({input:input});

            axios.post(API_BASE_URL+'api/employees',this.state.input)
                .then(function (response) {
                    console.log(response)
                    if(response.status === 201){
                        self.setState({'successMessage' : 'Employee successful Add...'});
                        self.handleClickEmp();     
                    }
                })
                .catch(function (error) {
                    console.log(error);
                });    
        }
    }

    handleClickEmp(){
        const { history } = this.props;
        this.props.history.push('/employee');
    }

      validate(){
        let input = this.state.input;
        let errors = {};
        let isValid = true;

        if (!input["employee_name"]) {
          isValid = false;
          errors["employee_name"] = "Please enter your employee name.";
        }

        if (!input["employee_salary"]) {
            isValid = false;
            errors["employee_salary"] = "Please enter your employee salary.";
        }

        if (typeof input["employee_salary"] !== "undefined") {

            var pattern = new RegExp(/^[0-9\b]+$/);
            if (!pattern.test(input["employee_salary"])) {
              isValid = false;
              errors["employee_salary"] = "Please enter only number.";
            }      
        }

        if (!input["employee_age"]) {
            isValid = false;
            errors["employee_age"] = "Please enter your employee age.";
        }

        if (typeof input["employee_age"] !== "undefined") {

            var pattern = new RegExp(/^[0-9\b]+$/);
            if (!pattern.test(input["employee_age"])) {
              isValid = false;
              errors["employee_age"] = "Please enter only number.";
            }

        } 

        if (!input["email"]) {
          isValid = false;
          errors["email"] = "Please enter your email Address.";
        }

        if (typeof input["email"] !== "undefined") {

          var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
          if (!pattern.test(input["email"])) {
            isValid = false;
            errors["email"] = "Please enter valid email address.";
          }
        }

        if (!input["mobile_number"]) {
          isValid = false;
          errors["mobile_number"] = "Please enter your phone number.";
        }

        if (typeof input["mobile_number"] !== "undefined") {

          var pattern = new RegExp(/^[0-9\b]+$/);
          if (!pattern.test(input["mobile_number"])) {
            isValid = false;
            errors["mobile_number"] = "Please enter only number.";
          }else if(input["mobile_number"].length != 10){
            isValid = false;
            errors["mobile_number"] = "Please enter valid phone number.";
          }
        }

        if (!input["address"]) {
          isValid = false;
          errors["address"] = "Please enter your comment.";
        }

        this.setState({
          errors: errors
        });

        return isValid;
    }

    render() { const {errors} = this.state;
    const { history } = this.props;   
return <div class="container">
          <div class="row">
                     <form onSubmit={this.handleSubmit} >
                        <div class="form-row">
                            <div class="form-group col-md-6">
                            <label for="inputEmail4">Employee Name</label>
                            <input type="text" name="employee_name" class="form-control" id="input4Email4" placeholder="Employee Name" value={this.state.input.employee_name}
                             onChange={this.handleChange}/>
                            <div className="text-danger">{this.state.errors.employee_name}</div>
                            </div>
                            <div class="form-group col-md-6">
                            <label for="inputPassword4">Employee Salary</label>
                            <input type="text" name="employee_salary" class="form-control" id="input4Password4" placeholder="Employee Salary" value={this.state.input.employee_salary}
                            onChange={this.handleChange} />
                            <div className="text-danger">{this.state.errors.employee_salary}</div>
                            </div>
                        </div>
                        <div class="form-row">
                            <div class="form-group col-md-6">
                            <label for="inputEmail4">Employee Age</label>
                            <input type="text" name="employee_age" class="form-control" id="inputEmail44" placeholder="Employee Age" value={this.state.input.employee_age}
                             onChange={this.handleChange} />
                            <div className="text-danger">{this.state.errors.employee_age}</div>
                            </div>
                            <div class="form-group col-md-6">
                            <label for="inputPassword4">Employee Mobule No</label>
                            <input type="text" name="mobile_number" class="form-control" id="inputPasswo4rd4" placeholder="Employee Mobule No" value={this.state.input.mobile_number}
                             onChange={this.handleChange} />
                            <div className="text-danger">{this.state.errors.mobile_number}</div>
                            </div>
                        </div>
                        <div class="form-row">
                            <div class="form-group col-md-6">
                            <label for="inputEmail4">Email</label>
                            <input type="email" name="email" class="form-control" id="ifnputEmail44" placeholder="Email" value={this.state.input.email}
                             onChange={this.handleChange} />
                            <div className="text-danger">{this.state.errors.email}</div>
                            </div>
                        </div>
                        <div class="form-group">
                            <label for="inputAddress">Address</label>
                            <input type="text" name="address" class="form-control" id="inputAddress" placeholder="1234 Main St" value={this.state.input.address}
                             onChange={this.handleChange} />
                            <div className="text-danger">{this.state.errors.address}</div>
                        </div>
                        <div class="form-group">
                            <div class="form-check">
                            <input class="form-check-input" type="checkbox" id="gridCheck"
                              />
                            <label class="form-check-label" for="gridCheck">
                                Check me out
                            </label>
                            </div>
                        </div>
                        <button type="submit" class="btn btn-primary">Sign in</button>
                     </form>                     
                   </div>

     </div>;
}

}

export default withRouter(EditEmployee);
