import React , { Component } from 'react';
import axios from 'axios';
import {API_BASE_URL } from '../../constants/apiConstants';
import { withRouter,Switch, Route, Link } from "react-router-dom";
//import {Route, IndexRoute,browserHistory} from 'react-router';
import { Router,browserHistory } from 'react-router';
import AddEmployee from '../AddEmployee/AddEmployee';

class Employee extends Component {
    constructor(props) {
        super(props);
        this.state = {
          //error: null,
          isLoaded: false,
          employees: []
        };
        this.getEmployees = this.getEmployees.bind(this);
        this.handleClick = this.handleClick.bind(this);
      }

      componentDidMount() {
        this.getEmployees();
      } 

    getEmployees= () => {
        let self =this;
        axios.get(API_BASE_URL+'api/employees')
        .then(function (response) {
            //console.log(response)
            if(response.status === 200){
                self.setState({ employees: response.data })
                //this.setState({employees:response})
            }
            //console.log(self.state.employees)
        })
        .catch(function (error) {
            console.log(error);
        });    
    }

    handleClick = () => {

    };

  render() {   
            const EmployeeData = this.state.employees.length > 0 && this.state.employees.map(function(employee, idx) {  
                const self = this;  
            return  <>
            <tr key={employee!= undefined && employee.id}>
            <td scope="row" >{idx+1}</td>
            <td>{employee!= undefined && employee.employee_name}</td>
            <td>{employee!= undefined && employee.employee_salary}</td>
            <td>{employee!= undefined && employee.employee_age}</td>
            <td> 
                <Link to={'/employee/edit/'+employee.id} className="btn btn-primary"> Edit </Link>   
                {/* <button class="btn btn-primary" href="#">Edit</button>/ */}
                <button class="btn btn-danger" onClick={() => this.handleClick()}>Delete</button>
             </td>    
            </tr>
            </>;   
          });

    return <div class="container">

         <Link to={'/employee/add/'} className="btn btn-success btn-lg float-right"> Add Employee </Link>
         <Switch>
              <Route exact path='/employee/add/' component={AddEmployee} />
          </Switch>

                <table class="table">

                <thead>
                    <tr>
                    <th scope="col">#</th>
                    <th scope="col">Employee Name</th>
                    <th scope="col">Salary</th>
                    <th scope="col">Age</th>
                    <th scope="col">Action</th>
                    </tr>
                </thead>
                <tbody>        
                     {EmployeeData}

                </tbody>
                </table>
         </div>;
  }
}

export default withRouter(Employee);
