import React , { Component } from 'react';
import axios from 'axios';
import {API_BASE_URL } from '../../constants/apiConstants';
import { withRouter } from "react-router-dom";

class Employee extends Component {
    constructor(props) {
        super(props);
        this.state = {
          //error: null,
          isLoaded: false,
          employees: []
        };
        this.getEmployees = this.getEmployees.bind(this);
        //this.sayHello = this.sayHello.bind(this);
      }

      componentDidMount() {
        this.getEmployees();
      } 

    getEmployees= () => {
        let self =this;
        axios.get(API_BASE_URL+'api/employees')
        .then(function (response) {
            //console.log(response)
            if(response.status === 200){
                self.setState({ employees: response.data })
                //this.setState({employees:response})
            }
            //console.log(self.state.employees)
        })
        .catch(function (error) {
            console.log(error);
        });    
    }

    onItemClick(event) {
        console.log(event.target.innerHTML)
      }

  render() {   
            const EmployeeData = this.state.employees.map(function(employee, idx) {  
                const self = this;  
            return  <>
            <tr key={idx}>
            <td scope="row" >{idx+1}</td>
            <td>{employee.employee_name}</td>
            <td>{employee.employee_salary}</td>
            <td>{employee.employee_age}</td>
            <td>
                <div class="dropdown">
                <button class="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                    Action
                </button>
                {/* <div class="dropdown-menu" aria-labelledby="dropdownMenuButton"> */}
                    <button class="dropdown-item" text={employee.id} onClick={this.onItemClick} key={idx}>Edit</button>/
                    <button class="dropdown-item" href="#">Delete</button>
                {/* </div> */}
                </div> 
            </td>
            </tr>
            </>;   
          });

    return <div class="container">
                <table class="table">
                <thead>
                    <tr>
                    <th scope="col">#</th>
                    <th scope="col">Employee Name</th>
                    <th scope="col">Salary</th>
                    <th scope="col">Age</th>
                    <th scope="col">Action</th>
                    </tr>
                </thead>
                <tbody>        
                     {EmployeeData}

                </tbody>
                </table>
         </div>;
  }
}

export default withRouter(Employee);
