import React, { useState,useEffect } from 'react';
import { render } from 'react-dom';
import axios from 'axios';
import { AgGridColumn, AgGridReact } from 'ag-grid-react';
import 'ag-grid-enterprise';
import 'ag-grid-community/dist/styles/ag-grid.css';
import 'ag-grid-community/dist/styles/ag-theme-alpine.css';
import {API_BASE_URL } from '../../constants/apiConstants';
import { useComponentWillMount } from '../../utils';
import * as agGrid from "ag-grid-community";
import BtnCellRenderer from './btn-cell-renderer';

const DataGrid = () => {
    const [gridApi, setGridApi] = useState(null);
    const [gridColumnApi, setGridColumnApi] = useState(null);
    const [rowData, setRowData] = useState([]);

    const [showModal, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);


    // const [rowData, setRowData] = useState([
    //     { make: "Toyota", model: "Celica", price: 35000 },
    //     { make: "Ford", model: "Mondeo", price: 32000 },
    //     { make: "Porsche", model: "Boxter", price: 72000 }
    // ]);

    const onGridReady = (params) => {
        setGridApi(params.api);
        setGridColumnApi(params.columnApi);

        const updateData = (data) => {
          setRowData(data);
        };
        //fetch('https://www.ag-grid.com/example-assets/olympic-winners.json')
         fetch(API_BASE_URL+'api/employees')
        .then((resp) => resp.json())
        .then((data) => updateData(data));
    };    

    // useEffect(() => {
    //     fetch('https://www.ag-grid.com/example-assets/row-data.json')
    //     .then(result => result.json())
    //     .then(rowData => setRowData(rowData))
    // }, []);

    const onButtonClick = e => {
        const selectedNodes = gridApi.getSelectedNodes()
        const selectedData = selectedNodes.map( node => node.data )
        const selectedDataStringPresentation = selectedData.map( node => `${node.employee_name} ${node.employee_age}`).join(', ')
        alert(`Selected nodes: ${selectedDataStringPresentation}`)
    }

    const onDeleteClick = e => {
        let self =this;
        const selectedNodes = gridApi.getSelectedNodes()
        const selectedData = selectedNodes.map( node => node.data )
        const selectedDataStringPresentation = selectedData.map((employeeData)=>{
            console.log(employeeData.id)
            let id = employeeData.id;
             axios.delete(API_BASE_URL+'api/employees/'+id)
             .then(function (response) {
                 if(response.status === 202){
                     //self.setState({ deleteMessage: response.data.message }) 
                     //self.getEmployees();
                     window.location.reload(); 
                 } 
             })
             .catch(function (error) {
                 console.log(error);
             });
        });
        //alert(`Selected nodes: ${selectedDataStringPresentation}`)
    } 


    return (<>
        <div className="ag-theme-alpine" style={{ height: 600, width: 1040 }}>
            <button className="btn btn-primary" onClick={onButtonClick}>Get selected rows</button>
            <button className="btn btn-danger" onClick={onDeleteClick}>Delete Rows</button>
            <button className="btn btn-warning" onClick={handleShow}>Edit Row</button>
            <AgGridReact
            rowSelection="multiple"
            defaultColDef={{
              width: 150,
              editable: true,
              filter: 'agTextColumnFilter',
              floatingFilter: true,
              resizable: true,
            }}
            defaultColGroupDef={{ marryChildren: true }}
            columnTypes={{
              numberColumn: {
                width: 130,
                filter: 'agNumberColumnFilter',
              },
              medalColumn: {
                width: 100,
                columnGroupShow: 'open',
                filter: false,
              },
              nonEditableColumn: { editable: false },
              dateColumn: {
                filter: 'agDateColumnFilter',
                filterParams: {
                  comparator: function (filterLocalDateAtMidnight, cellValue) {
                    var dateParts = cellValue.split('/');
                    var day = Number(dateParts[0]);
                    var month = Number(dateParts[1]) - 1;
                    var year = Number(dateParts[2]);
                    var cellDate = new Date(year, month, day);
                    if (cellDate < filterLocalDateAtMidnight) {
                      return -1;
                    } else if (cellDate > filterLocalDateAtMidnight) {
                      return 1;
                    } else {
                      return 0;
                    }
                  },
                },
              },
            }}
            rowData={rowData}
            onGridReady={onGridReady}
          >
            <AgGridColumn headerName="Employee Name" field="employee_name" checkboxSelection={ true }/>
            <AgGridColumn headerName="Mobile No." field="mobile_number" type="numberColumn"/>
            <AgGridColumn headerName="Employee Age" field="employee_age" type="numberColumn" />
            <AgGridColumn headerName="Email" field="email" />
            <AgGridColumn
              headerName="Date"
              field="date"
              type={['dateColumn', 'nonEditableColumn']}
              width={220}
            />
            <AgGridColumn
                headerName="Delete"
                field="employee_name"
                cellRenderer = "btnCellRenderer" 
                // cellRendererParams = {
                //     clicked = function(field) {
                //     alert(`${field} was clicked`);
                //     }
                // }
                minWidth= {150}
              />
            {/* <AgGridColumn headerName="Medals" groupId="medalsGroup">
              <AgGridColumn headerName="Gold" field="gold" type="medalColumn" />
              <AgGridColumn
                headerName="Silver"
                field="silver"
                type="medalColumn"
              />
              <AgGridColumn
                headerName="Bronze"
                field="bronze"
                type="medalColumn"
              />
              <AgGridColumn
                headerName="Total"
                field="total"
                type="medalColumn"
                columnGroupShow="closed"
              />
            </AgGridColumn> */}
          </AgGridReact>
        </div>
        <div className="modal" tabIndex="-1" show={showModal} onHide={handleClose}>
  <div className="modal-dialog">
    <div className="modal-content">
      <div className="modal-header">
        <h5 className="modal-title">Modal title</h5>
        <button type="button" className="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body">
        <p>Modal body text goes here.</p>
      </div>
      <div className="modal-footer">
        <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" className="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>
        </>
    );
};

export default DataGrid;
