import React, {useState,useEffect} from 'react';
import axios from 'axios';
import { BrowserRouter as Router,Switch,Route,useParams,withRouter } from "react-router-dom";
import { API_BASE_URL,ACCESS_TOKEN_NAME } from '../../constants/apiConstants';
import { Navbar,Nav,NavDropdown,Form,FormControl,Button } from 'react-bootstrap'
import LoginForm from '../LoginForm/LoginForm';
import RegistrationForm from '../RegistrationForm/RegistrationForm';
import Home from '../Home/Home';
import HookState from '../HookState/HookState';
import CounterExample from '../CounterExample/CounterExample';
import CustomCounter from '../CustomCounter/CustomCounter';
import Parent from '../ParenttoChild/Parent';
import Child from '../ChildtoParent/Child';
import Employee from '../Employee/Employee';
import AddEmployee from '../AddEmployee/AddEmployee';
import EditEmployee from '../EditEmployee/EditEmployee';

function Header(props) {
    const [state , setState] = useState({
        successMessage: null,
        tokenSet:''
    })
    const capitalize = (s) => {
        if (typeof s !== 'string') return ''
        return s.charAt(0).toUpperCase() + s.slice(1)
    }
    let title = capitalize(props.location.pathname.substring(1,props.location.pathname.length))
    if(props.location.pathname === '/') {
        title = 'Welcome'
    }

    const redirectToLogin = () => {
        //props.updateTitle('Login')
        props.history.push('/login'); 
    }


    const getLocalTodo = () => {
         //debugger
         console.log(state.tokenSet)
        if (localStorage.getItem(ACCESS_TOKEN_NAME) === null) {
          localStorage.setItem(ACCESS_TOKEN_NAME, JSON.stringify([]));
        } else {
          let todoLocal = localStorage.getItem(ACCESS_TOKEN_NAME);
          setState(prevState => ({
            ...prevState,
            'tokenSet' : todoLocal
            }))
        }
      };
      useEffect(() => {
        getLocalTodo();
      }, []);

    // useEffect(() => {
    //     if (localStorage.getItem(ACCESS_TOKEN_NAME) === null) {
    //         localStorage.setItem(ACCESS_TOKEN_NAME, JSON.stringify([]));
    //       } else { 
    //           setState(prevState => ({
    //         ...prevState,
    //         'tokenSet' : localStorage.getItem(ACCESS_TOKEN_NAME)
    //         }))
    //        }
    //   });

    //function renderLogout() {
        // //if(props.location.pathname === '/home'){
        //   if(state.tokenSet){  
        //     return(
        //         <div className="ml-auto">
        //             <button className="btn btn-danger" onClick={() => handleLogout()}>Logout</button>
        //         </div>
        //     )
        // }
    //}

    function handleLogout() {
        var token_base64= localStorage.getItem(ACCESS_TOKEN_NAME);
        alert(token_base64.length)
        alert(token_base64)
        debugger
        if(token_base64.length===2 || token_base64===null || token_base64===undefined){
            redirectToLogin()
          }
        var token = atob(token_base64);
        let header = {
            'Authorization': `Bearer ${token}`,
          };
            axios.post(API_BASE_URL+'api/auth/logout',{}, { headers: header})
            .then(function (response) {
                if(response.status==200){
                    setState(prevState => ({
                        ...prevState,
                        'successMessage' : response.data.message
                    }))
                    localStorage.removeItem(ACCESS_TOKEN_NAME)
                    props.history.push('/login')
                }
                if(response.status !== 200){
                    props.history.push('/home')
                }
            })
            .catch(function (error) {
                props.history.push('/home')
            });    
    }
    return(
        <Router>
            <Navbar bg="dark" variant="dark" expand="lg" sticky="top">
                <Navbar.Brand href="#home">React Bootstrap Navbar</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="mr-auto">
                    <Nav.Link href="/">Home</Nav.Link>
                    <Nav.Link href="/hookstate">Hook State</Nav.Link>
                    <Nav.Link href="/custume">Custume</Nav.Link>
                    <Nav.Link href="/employee">Employee</Nav.Link>
                    <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                        <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                        <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                        <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                        <NavDropdown.Divider />
                        <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
                    </NavDropdown>
                    <div className="row col-12 d-flex justify-content-center text-white">
                    {(state.tokenSet!=null && state.tokenSet!=undefined) ? <div className="ml-auto"><button className="btn btn-danger" onClick={() => handleLogout()}>Logout</button></div> : <div className="ml-auto"><button className="btn btn-success" onClick={() => redirectToLogin()}>Login</button></div>}   
                    {/* {renderLogout()} */}
                    </div>
                    </Nav>
                    <Form inline>
                    <FormControl type="text" placeholder="Search" className="mr-sm-2" />
                    <Button variant="outline-success">Search</Button>
                    </Form>
                </Navbar.Collapse>
            </Navbar>
            <br />
        </Router>
        // <nav className="navbar navbar-dark bg-primary">
        //     <div className="row col-12 d-flex justify-content-center text-white">
        //         <span className="h3">{props.title || title}</span>
        //         {renderLogout()}
        //     </div>
        // </nav>
    )
}
export default withRouter(Header);
