import React , { Component } from 'react';
import axios from 'axios';
import {API_BASE_URL } from '../../constants/apiConstants';
import { withRouter,Switch, Route, Link } from "react-router-dom";
//import {Route, IndexRoute,browserHistory} from 'react-router';
import { Router,browserHistory } from 'react-router';
import './AddEmployee.css';

const validEmailRegex = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);
const validateForm = (errors) => {
  let valid = true;
  Object.values(errors).forEach(
    (val) => val.length > 0 && (valid = false)
  );
  return valid;
}

class AddEmployee extends Component {

    constructor(props) {
        super(props);
        this.state = {
            input: {},
            errors: {}
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

    handleChange(event) {
        let input = this.state.input;
        input[event.target.name] = event.target.value;
        this.setState({
          input
        });
    }

      handleSubmit(event) {
        event.preventDefault();

        if(this.validate()){
            console.log(this.state);

            let input = {};
            input["employee_name"] = "";
            input["employee_salary"] = "";
            input["employee_age"] = "";
            input["email"] = "";
            input["mobile_number"] = "";
            input["address"] = "";
            this.setState({input:input});

            alert('Demo Form is submited');
        }
      }

      validate(){
        let input = this.state.input;
        let errors = {};
        let isValid = true;

        if (!input["name"]) {
          isValid = false;
          errors["name"] = "Please enter your name.";
        }

        if (!input["email"]) {
          isValid = false;
          errors["email"] = "Please enter your email Address.";
        }

        if (typeof input["email"] !== "undefined") {

          var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
          if (!pattern.test(input["email"])) {
            isValid = false;
            errors["email"] = "Please enter valid email address.";
          }
        }

        if (!input["phone"]) {
          isValid = false;
          errors["phone"] = "Please enter your phone number.";
        }

        if (typeof input["phone"] !== "undefined") {

          var pattern = new RegExp(/^[0-9\b]+$/);
          if (!pattern.test(input["phone"])) {
            isValid = false;
            errors["phone"] = "Please enter only number.";
          }else if(input["phone"].length != 10){
            isValid = false;
            errors["phone"] = "Please enter valid phone number.";
          }
        }

        if (!input["comment"]) {
          isValid = false;
          errors["comment"] = "Please enter your comment.";
        }

        this.setState({
          errors: errors
        });

        return isValid;
    }


    render() {  const {errors} = this.state;
        const { history } = this.props;   
        return <div class="container">
                   <div class="row">
                     <form onSubmit={this.handleSubmit} noValidate>
                        <div class="form-row">
                            <div class="form-group col-md-6">
                            <label for="inputEmail4">Employee Name</label>
                            <input type="text" name="employee_name" class="form-control" id="input4Email4" placeholder="Employee Name" onChange={this.handleChange}/>

                            </div>
                            <div class="form-group col-md-6">
                            <label for="inputPassword4">Employee Salary</label>
                            <input type="text" name="employee_salary" class="form-control" id="input4Password4" placeholder="Employee Salary" onChange={this.handleChange} noValidate/>

                            </div>
                        </div>
                        <div class="form-row">
                            <div class="form-group col-md-6">
                            <label for="inputEmail4">Employee Age</label>
                            <input type="text" name="employee_age" class="form-control" id="inputEmail44" placeholder="Employee Age" onChange={this.handleChange} noValidate/>

                            </div>
                            <div class="form-group col-md-6">
                            <label for="inputPassword4">Employee Mobule No</label>
                            <input type="text" name="mobile_number" class="form-control" id="inputPasswo4rd4" placeholder="Employee Mobule No" onChange={this.handleChange} noValidate/>

                            </div>
                        </div>
                        <div class="form-row">
                            <div class="form-group col-md-6">
                            <label for="inputEmail4">Email</label>
                            <input type="email" name="email" class="form-control" id="ifnputEmail44" placeholder="Email" onChange={this.handleChange} noValidate/>

                            </div>
                        </div>
                        <div class="form-group">
                            <label for="inputAddress">Address</label>
                            <input type="text" name="address" class="form-control" id="inputAddress" placeholder="1234 Main St" onChange={this.handleChange} noValidate/>

                        </div>
                        <div class="form-group">
                            <div class="form-check">
                            <input class="form-check-input" type="checkbox" id="gridCheck" onChange={this.handleChange} noValidate/>
                            <label class="form-check-label" for="gridCheck">
                                Check me out
                            </label>
                            </div>
                        </div>
                        <button type="submit" class="btn btn-primary">Sign in</button>
                     </form>                     
                   </div>
                </div>;
    }
}
export default withRouter(AddEmployee);
