import React, { Component } from 'react';
import axios from 'axios';
import { API_BASE_URL } from '../../constants/apiConstants';
import { withRouter, Switch, Route, Link } from "react-router-dom";



class MultipleImageUpload extends Component {

    fileObj = [];
    fileArray = [];
    fileArrayUrl = [];

    constructor(props) {
        super(props)
        this.state = {
            file: [null],
            files: [],
            successMessage:''
        }
        this.uploadMultipleFiles = this.uploadMultipleFiles.bind(this)
        this.uploadFiles = this.uploadFiles.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    uploadMultipleFiles(e) {
        //debugger
        this.fileObj.push(e.target.files)
        for (let i = 0; i < this.fileObj[0].length; i++) {
            this.fileArrayUrl.push(URL.createObjectURL(this.fileObj[0][i]))
            this.fileArray.push(this.fileObj[0][i])
        }
        this.setState({ file: this.fileArray })
        //console.log(this.fileArray)
        console.log(this.fileArray)
        //console.log(this.state.file)
    }

    uploadFiles(e) {
        e.preventDefault()
        console.log(this.state.file)
    }

    fileSelectedHandler = (e) => {
        this.setState({ files: [...this.state.files, ...e.target.files] })
      }

    handleSubmit(event) {
        event.preventDefault();
        let self = this;
        console.log(this.state.file);

          // Create an object of formData
          const formData = new FormData();
          //Update the formData object
          formData.append("fileName", this.state.file);

          axios.post(API_BASE_URL +'api/multiple-image-upload',formData)
            .then(function (response) {
              console.log(response)
              if (response.status === 200) {
                self.setState({ 'successMessage': 'Images successful Add...' });
                //self.handleClickEmp();
              }
            })
            .catch(function (error) {
              console.log(error);
            });  
      }

    render() {
        return (<>
            <form onSubmit={this.handleSubmit} encType="multipart/form-data">
                <div className="form-group multi-preview">
                    {(this.fileArrayUrl || []).map(url => (
                        <img key={url} src={url} alt="..." />
                    ))}
                </div>

                <div className="form-group">
                    <input type="file" className="form-control" name="fileName" onChange={this.uploadMultipleFiles} multiple />
                </div>
                <button type="submit" className="btn btn-danger btn-block" >Upload</button>
            </form >
            <form>
                <div><h2>Upload images</h2></div>
                <h3>Images</h3>
                <input type="file" multiple onChange={this.fileSelectedHandler} />
          </form>
           </> 
        )
    }
}
export default withRouter(MultipleImageUpload);
